import jsPDF from "jspdf";

export const downloadInvoicePDF = (customerName, items, grandTotal) => {
  const doc = new jsPDF();

  doc.setFontSize(18);
  doc.text("GANESH TRADERS", 105, 20, { align: "center" });

  doc.setFontSize(11);
  doc.text("Cement, Steel & Building Materials", 105, 28, { align: "center" });

  doc.line(10, 32, 200, 32);

  doc.text(`Party Name: ${customerName}`, 10, 42);
  doc.text(`Date: ${new Date().toLocaleDateString()}`, 150, 42);

  let y = 55;

  doc.text("Item", 10, y);
  doc.text("Qty", 75, y);
  doc.text("Unit", 95, y);
  doc.text("Price", 125, y);
  doc.text("Total", 165, y);

  doc.line(10, y + 2, 200, y + 2);

  y += 10;
  items.forEach((item) => {
    doc.text(item.itemName, 10, y);
    doc.text(String(item.quantity), 75, y);
    doc.text(item.unit, 95, y);
    doc.text(`‚Çπ ${item.price}`, 125, y);
    doc.text(`‚Çπ ${item.total}`, 165, y);
    y += 8;
  });

  doc.line(10, y, 200, y);

  y += 10;
  doc.setFontSize(13);
  doc.text(`Grand Total: ‚Çπ ${grandTotal}`, 140, y);

  y += 15;
  doc.setFontSize(10);
  doc.text("Thank you! Visit Again üôè", 105, y, { align: "center" });

  doc.save("Ganesh_Traders_Bill.pdf");
};
