import jsPDF from "jspdf";

export const downloadInvoicePDF = (customerName, items, grandTotal) => {
  const doc = new jsPDF("p", "mm", "a4");

  const pageWidth = doc.internal.pageSize.getWidth();

  /* ================= HEADER ================= */
  doc.setFontSize(18);
  doc.setFont("helvetica", "bold");
  doc.text("GANESH TRADERS", pageWidth / 2, 20, { align: "center" });

  doc.setFontSize(11);
  doc.setFont("helvetica", "normal");
  doc.text(
    "Cement ‚Ä¢ Steel ‚Ä¢ Building Materials",
    pageWidth / 2,
    27,
    { align: "center" }
  );

  doc.line(10, 32, pageWidth - 10, 32);

  /* ================= PARTY INFO ================= */
  doc.setFontSize(11);
  doc.setFont("helvetica", "bold");
  doc.text("Party Name:", 10, 42);
  doc.setFont("helvetica", "normal");
  doc.text(customerName || "-", 35, 42);

  doc.setFont("helvetica", "bold");
  doc.text("Date:", pageWidth - 50, 42);
  doc.setFont("helvetica", "normal");
  doc.text(new Date().toLocaleDateString(), pageWidth - 30, 42);

  /* ================= TABLE HEADER ================= */
  let y = 55;

  doc.setFontSize(11);
  doc.setFont("helvetica", "bold");

  doc.rect(10, y - 6, pageWidth - 20, 10);
  doc.text("Item", 12, y);
  doc.text("Qty", 90, y);
  doc.text("Unit", 105, y);
  doc.text("Rate", 130, y);
  doc.text("Amount", 165, y);

  y += 10;
  doc.setFont("helvetica", "normal");

  /* ================= TABLE BODY ================= */
  items.forEach((item) => {
    if (y > 270) {
      doc.addPage();
      y = 20;
    }

    doc.text(item.itemName, 12, y);
    doc.text(String(item.quantity), 92, y);
    doc.text(item.unit, 107, y);
    doc.text(`‚Çπ ${item.price}`, 130, y);
    doc.text(`‚Çπ ${item.total}`, 165, y);

    y += 8;
  });

  /* ================= TOTAL ================= */
  y += 5;
  doc.line(120, y, pageWidth - 10, y);

  y += 8;
  doc.setFontSize(13);
  doc.setFont("helvetica", "bold");
  doc.text("Grand Total", 120, y);
  doc.text(`‚Çπ ${grandTotal}`, pageWidth - 15, y, { align: "right" });

  /* ================= FOOTER ================= */
  y += 20;
  doc.setFontSize(10);
  doc.setFont("helvetica", "normal");
  doc.text(
    "Thank you for your business!",
    pageWidth / 2,
    y,
    { align: "center" }
  );
  doc.text(
    "Visit Again üôè",
    pageWidth / 2,
    y + 6,
    { align: "center" }
  );

  /* ================= FILE NAME ================= */
  const safeName = customerName
    ? customerName.replace(/[^a-zA-Z0-9]/g, "_")
    : "Invoice";

  doc.save(`${safeName}.pdf`);
};
