import { useState } from "react";
import { downloadInvoicePDF } from "./InvoicePDF";

function InvoiceForm() {
  const [customerName, setCustomerName] = useState("");
  const [itemName, setItemName] = useState("");
  const [quantity, setQuantity] = useState("");
  const [unit, setUnit] = useState("Bags");
  const [customUnit, setCustomUnit] = useState("");
  const [price, setPrice] = useState("");
  const [items, setItems] = useState([]);

  const finalUnit = unit === "Others" ? customUnit : unit;

  const addItem = () => {
    if (!itemName || !quantity || !price) return;
    if (unit === "Others" && !customUnit) return;

    const total = Number(quantity) * Number(price);

    setItems([
      ...items,
      { itemName, quantity, unit: finalUnit, price, total },
    ]);

    setItemName("");
    setQuantity("");
    setPrice("");
    setUnit("Bags");
    setCustomUnit("");
  };

  const grandTotal = items.reduce((sum, item) => sum + item.total, 0);

  return (
    <div className="min-h-screen bg-slate-100 py-10 px-4">

      {/* COMPANY HEADER */}
      <div className="max-w-6xl mx-auto bg-white rounded-2xl shadow-lg p-8 mb-8 border-l-8 border-orange-600">
        <h1 className="text-4xl font-bold text-slate-800 tracking-wide">
          GANESH TRADERS
        </h1>
        <p className="text-slate-500 mt-1">
          Cement • Steel • Building Materials
        </p>
      </div>

      {/* PARTY DETAILS */}
      <div className="max-w-6xl mx-auto bg-white rounded-2xl shadow-md p-6 mb-8">
        <h2 className="text-lg font-semibold text-slate-700 mb-4">
          Party Information
        </h2>
        <input
          type="text"
          placeholder="Enter Party / Customer Name"
          value={customerName}
          onChange={(e) => setCustomerName(e.target.value)}
          className="w-full border border-slate-300 rounded-lg px-4 py-2 focus:ring-2 focus:ring-orange-500 focus:outline-none"
        />
      </div>

      {/* ADD ITEM */}
      <div className="max-w-6xl mx-auto bg-white rounded-2xl shadow-md p-6 mb-8">
        <h2 className="text-lg font-semibold text-slate-700 mb-6">
          Add Item Details
        </h2>

        <div className="grid grid-cols-1 md:grid-cols-7 gap-4 items-end">
          <input
            type="text"
            placeholder="Item Name"
            value={itemName}
            onChange={(e) => setItemName(e.target.value)}
            className="border border-slate-300 rounded-lg px-3 py-2"
          />

          <select
            value={unit}
            onChange={(e) => setUnit(e.target.value)}
            className="border border-slate-300 rounded-lg px-3 py-2"
          >
            <option>Bags</option>
            <option>Kg</option>
            <option>Nos</option>
            <option>Meter</option>
            <option>Box</option>
            <option>Set</option>
            <option>Others</option>
          </select>

          {unit === "Others" && (
            <input
              type="text"
              placeholder="Custom Unit"
              value={customUnit}
              onChange={(e) => setCustomUnit(e.target.value)}
              className="border border-slate-300 rounded-lg px-3 py-2"
            />
          )}

          <input
            type="number"
            placeholder="Quantity"
            value={quantity}
            onChange={(e) => setQuantity(e.target.value)}
            className="border border-slate-300 rounded-lg px-3 py-2"
          />

          <input
            type="number"
            placeholder="Rate"
            value={price}
            onChange={(e) => setPrice(e.target.value)}
            className="border border-slate-300 rounded-lg px-3 py-2"
          />

          <button
            onClick={addItem}
            className="md:col-span-2 bg-orange-600 text-white font-semibold rounded-lg px-4 py-2 hover:bg-orange-700 transition"
          >
            + Add Item
          </button>
        </div>
      </div>

      {/* BILL TABLE */}
      {items.length > 0 && (
        <div className="max-w-6xl mx-auto bg-white rounded-2xl shadow-lg p-6">
          <div className="flex justify-between mb-6">
            <p className="text-slate-700">
              <strong>Party:</strong> {customerName}
            </p>
            <p className="text-slate-500">
              Date: {new Date().toLocaleDateString()}
            </p>
          </div>

          <div className="overflow-x-auto">
            <table className="w-full border border-slate-300 text-sm">
              <thead className="bg-slate-200 text-slate-700">
                <tr>
                  <th className="border px-3 py-2">Item</th>
                  <th className="border px-3 py-2">Qty</th>
                  <th className="border px-3 py-2">Unit</th>
                  <th className="border px-3 py-2">Rate</th>
                  <th className="border px-3 py-2">Amount</th>
                </tr>
              </thead>
              <tbody>
                {items.map((item, index) => (
                  <tr key={index} className="text-center hover:bg-slate-50">
                    <td className="border px-3 py-2">{item.itemName}</td>
                    <td className="border px-3 py-2">{item.quantity}</td>
                    <td className="border px-3 py-2">{item.unit}</td>
                    <td className="border px-3 py-2">₹ {item.price}</td>
                    <td className="border px-3 py-2 font-medium">
                      ₹ {item.total}
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>

          <div className="flex justify-end mt-6">
            <div className="bg-slate-100 px-6 py-4 rounded-xl text-right">
              <p className="text-slate-600 text-sm">Grand Total</p>
              <p className="text-3xl font-bold text-slate-800">
                ₹ {grandTotal}
              </p>
            </div>
          </div>

          <div className="flex justify-end mt-6">
            <button
              onClick={() =>
                downloadInvoicePDF(customerName, items, grandTotal)
              }
              className="bg-blue-600 text-white font-semibold px-8 py-3 rounded-lg hover:bg-blue-700 transition"
            >
              Download Invoice PDF
            </button>
          </div>
        </div>
      )}
    </div>
  );
}

export default InvoiceForm;
