import { useState, useEffect } from "react";
import { downloadInvoicePDF } from "./InvoicePDF";
import InvoiceTemplate from "./InvoiceTemplate";

function InvoiceForm() {
  // ----------------- CUSTOMER STATES -----------------
  const [customerName, setCustomerName] = useState("");
  const [phoneNumber, setPhoneNumber] = useState("");
  const [customers, setCustomers] = useState(() => {
    return JSON.parse(localStorage.getItem("customers")) || [];
  });
  const [selectedCustomer, setSelectedCustomer] = useState("");

  // ----------------- ITEM STATES -----------------
  const [itemName, setItemName] = useState("");
  const [quantity, setQuantity] = useState("");
  const [unit, setUnit] = useState("Bags");
  const [customUnit, setCustomUnit] = useState("");
  const [price, setPrice] = useState("");
  const [items, setItems] = useState([]);

  const finalUnit = unit === "Others" ? customUnit : unit;

  // ----------------- INVOICE / ANALYTICS -----------------
  const [invoices, setInvoices] = useState(() => {
    return JSON.parse(localStorage.getItem("invoices")) || [];
  });

  const grandTotal = items.reduce((sum, item) => sum + item.total, 0);

  // ----------------- EFFECTS -----------------
  useEffect(() => {
    if (selectedCustomer) {
      const customer = customers.find((c) => c.name === selectedCustomer);
      if (customer) {
        setCustomerName(customer.name);
        setPhoneNumber(customer.phone);
      }
    }
  }, [selectedCustomer]);

  // ----------------- FUNCTIONS -----------------
  const addItem = () => {
    if (!itemName || !quantity || !price) return;
    if (unit === "Others" && !customUnit) return;

    const total = Number(quantity) * Number(price);

    setItems([
      ...items,
      { itemName, quantity, unit: finalUnit, price, total },
    ]);

    setItemName("");
    setQuantity("");
    setPrice("");
    setUnit("Bags");
    setCustomUnit("");
  };

  const saveCustomer = () => {
    if (!customerName) return;
    const exists = customers.find((c) => c.name === customerName);
    if (!exists) {
      const newCustomer = { name: customerName, phone: phoneNumber };
      const updatedCustomers = [...customers, newCustomer];
      setCustomers(updatedCustomers);
      localStorage.setItem("customers", JSON.stringify(updatedCustomers));
    }
  };

  const saveInvoice = () => {
    const invoiceData = {
      customerName,
      phoneNumber,
      items,
      grandTotal,
      date: new Date().toISOString(),
    };
    const updatedInvoices = [...invoices, invoiceData];
    setInvoices(updatedInvoices);
    localStorage.setItem("invoices", JSON.stringify(updatedInvoices));
  };

  const handleDownloadPDF = () => {
    saveCustomer();
    saveInvoice();
    downloadInvoicePDF("invoice-pdf", customerName);
  };

  // ----------------- ANALYTICS -----------------
  const totalSales = invoices.reduce((sum, inv) => sum + inv.grandTotal, 0);

  const itemCount = {};
  invoices.forEach((inv) => {
    inv.items.forEach((item) => {
      itemCount[item.itemName] = (itemCount[item.itemName] || 0) + Number(item.quantity);
    });
  });
  const topItems = Object.entries(itemCount).sort((a, b) => b[1] - a[1]).slice(0, 5);

  const customerInvoices = {};
  invoices.forEach((inv) => {
    customerInvoices[inv.customerName] = (customerInvoices[inv.customerName] || 0) + 1;
  });

  return (
    <div className="min-h-screen bg-slate-100 py-10 px-4">
      {/* HEADER */}
      <div className="max-w-6xl mx-auto bg-white rounded-2xl shadow-lg p-8 mb-8 border-l-8 border-orange-600">
        <h1 className="text-4xl font-bold text-slate-800">
          GANESH TRADERS
        </h1>
        <p className="text-slate-500">
          Cement • Steel • Electrical • Sanitary Items
        </p>
      </div>

      {/* PARTY / CUSTOMER */}
      <div className="max-w-6xl mx-auto bg-white rounded-2xl shadow p-6 mb-8">
        <h2 className="font-semibold mb-3">Party Information</h2>

        {/* Select Existing Customer */}
        {customers.length > 0 && (
          <select
            className="w-full border rounded-lg px-4 py-2 mb-3"
            value={selectedCustomer}
            onChange={(e) => setSelectedCustomer(e.target.value)}
          >
            <option value="">Select Existing Customer</option>
            {customers.map((c, i) => (
              <option key={i} value={c.name}>
                {c.name}
              </option>
            ))}
          </select>
        )}

        <input
          className="w-full border rounded-lg px-4 py-2 mb-3"
          placeholder="Party / Customer Name"
          value={customerName}
          onChange={(e) => setCustomerName(e.target.value)}
        />

        <input
          className="w-full border rounded-lg px-4 py-2"
          placeholder="Phone Number"
          value={phoneNumber}
          onChange={(e) => setPhoneNumber(e.target.value)}
        />
      </div>

      {/* ADD ITEM */}
      <div className="max-w-6xl mx-auto bg-white rounded-2xl shadow p-6 mb-8">
        <h2 className="font-semibold mb-4">Add Item</h2>

        <div className="grid grid-cols-1 md:grid-cols-7 gap-4">
          <input
            className="border rounded-lg px-3 py-2"
            placeholder="Item Name"
            value={itemName}
            onChange={(e) => setItemName(e.target.value)}
          />

          <select
            className="border rounded-lg px-3 py-2"
            value={unit}
            onChange={(e) => setUnit(e.target.value)}
          >
            <option>Bags</option>
            <option>Kg</option>
            <option>Nos</option>
            <option>Meter</option>
            <option>Box</option>
            <option>Set</option>
            <option>Others</option>
          </select>

          {unit === "Others" && (
            <input
              className="border rounded-lg px-3 py-2"
              placeholder="Custom Unit"
              value={customUnit}
              onChange={(e) => setCustomUnit(e.target.value)}
            />
          )}

          <input
            className="border rounded-lg px-3 py-2"
            type="number"
            placeholder="Qty"
            value={quantity}
            onChange={(e) => setQuantity(e.target.value)}
          />

          <input
            className="border rounded-lg px-3 py-2"
            type="number"
            placeholder="Rate"
            value={price}
            onChange={(e) => setPrice(e.target.value)}
          />

          <button
            onClick={addItem}
            className="md:col-span-2 bg-orange-600 text-white rounded-lg"
          >
            + Add Item
          </button>
        </div>
      </div>

      {/* PDF PREVIEW */}
      {items.length > 0 && (
        <>
          <div
            id="invoice-pdf"
            className="max-w-6xl mx-auto bg-white rounded-2xl shadow p-6"
          >
            <InvoiceTemplate
              customerName={customerName}
              phoneNumber={phoneNumber}
              items={items}
              grandTotal={grandTotal}
            />
          </div>

          <div className="max-w-6xl mx-auto flex justify-end mt-6">
            <button
              onClick={handleDownloadPDF}
              className="bg-blue-600 text-white px-8 py-3 rounded-lg"
            >
              Download Invoice PDF
            </button>
          </div>
        </>
      )}

      {/* ANALYTICS DASHBOARD */}
      {invoices.length > 0 && (
        <div className="max-w-6xl mx-auto p-6 bg-white rounded-2xl shadow mt-10">
          <h2 className="text-xl font-bold mb-4">Analytics Dashboard</h2>

          <p>Total Sales: ₹ {totalSales}</p>

          <div className="mt-4">
            <h3 className="font-semibold">Top Items:</h3>
            <ul>
              {topItems.map(([item, qty]) => (
                <li key={item}>
                  {item} - {qty} units
                </li>
              ))}
            </ul>
          </div>

          <div className="mt-4">
            <h3 className="font-semibold">Customer Stats:</h3>
            <ul>
              {Object.entries(customerInvoices).map(([name, count]) => (
                <li key={name}>
                  {name} - {count} invoice(s)
                </li>
              ))}
            </ul>
          </div>
        </div>
      )}
    </div>
  );
}

export default InvoiceForm;
